import React, { useReducer, useState } from "react";
import Appointment from "./Appointment";
import UpdateAppointment from "./UpdateAppointment";

const initialState = [
  { id: 1, time: "9am", patientId: 1, doctorId: 1, address: "123 Main St" },
  { id: 2, time: "2pm", patientId: 2, doctorId: 2, address: "456 Elm St" },
  { id: 3, time: "4pm", patientId: 3, doctorId: 3, address: "789 Oak St" }
];

const CREATE_APPOINTMENT = "CREATE_APPOINTMENT";
const UPDATE_APPOINTMENT = "UPDATE_APPOINTMENT";
const DELETE_APPOINTMENT = "DELETE_APPOINTMENT";
const EDIT_APPOINTMENT = "EDIT_APPOINTMENT";
const CANCEL_EDIT = "CANCEL_EDIT";

function reducer(state, action) {
  switch (action.type) {
    case CREATE_APPOINTMENT:
      return [...state, action.payload];
    case UPDATE_APPOINTMENT:
      return state.map((appointment) =>
        appointment.id === action.payload.id
          ? { ...appointment, ...action.payload }
          : appointment
      );
    case DELETE_APPOINTMENT:
      return state.filter((appointment) => appointment.id !== action.payload);
    default:
      return state;
  }
}

function App() {
  const [appointments, dispatch] = useReducer(reducer, initialState);
  const [editingAppointment, setEditingAppointment] = useState(null);

  const handleCreateAppointment = (newAppointment) => {
    dispatch({ type: CREATE_APPOINTMENT, payload: newAppointment });
  };

  const handleUpdateAppointment = (id, newTime, newAddress) => {
    dispatch({
      type: UPDATE_APPOINTMENT,
      payload: { id, time: newTime, address: newAddress }
    });
    setEditingAppointment(null);
  };

  const handleDeleteAppointment = (id) => {
    dispatch({ type: DELETE_APPOINTMENT, payload: id });
  };

  const handleEditAppointment = (id) => {
    setEditingAppointment(id);
  };

  const handleCancelEdit = () => {
    setEditingAppointment(null);
  };

  return (
    <div>
      <h1>Appointments</h1>
      <Appointments
        appointments={appointments}
        editingAppointment={editingAppointment}
        onEditAppointment={handleEditAppointment}
        onDeleteAppointment={handleDeleteAppointment}
        onUpdateAppointment={handleUpdateAppointment}
        onCancelEdit={handleCancelEdit}
      />
    </div>
  );
}

function Appointments({
  appointments,
  editingAppointment,
  onEditAppointment,
  onDeleteAppointment,
  onUpdateAppointment,
  onCancelEdit
}) {
  return (
    <>
      {appointments.map((appointment) => (
        <div key={appointment.id}>
          {editingAppointment === appointment.id ? (
            <UpdateAppointment
              appointment={appointment}
              onUpdateAppointment={onUpdateAppointment}
              onCancelEdit={handleCancelEdit} // Fixed error
            />
          ) : (
            <Appointment
              appointment={appointment}
              onEditAppointment={onEditAppointment}
              onDeleteAppointment={onDeleteAppointment}
            />
          )}
        </div>
      ))}
    </>
  );
}
